prior{im}.y=[0:dx:13];
prior{im}.cax=[.1 .18];
i_master=im;

% range - horizontal
im=im+1;
prior{im}.type='gaussian';
prior{im}.name='range_1';
prior{im}.min=1.5;
prior{im}.max=10.5;
prior{im}.norm=50;
prior{im}.prior_master=i_master;

% range - horizontal
im=im+1;
prior{im}.type='gaussian';
prior{im}.name='range_2';
prior{im}.min=1.5;
prior{im}.max=5.5;
prior{im}.norm=50;
prior{im}.prior_master=i_master;


% rotation
im=im+1;
prior{im}.type='gaussian';
prior{im}.name='ang_1';
prior{im}.m0=90;
prior{im}.std=20;
prior{im}.norm=2;
prior{im}.prior_master=i_master;

